        $array3[] = $array1[$i] + $array2[$i];
    } else {
        $array3[] = $array1[$i]; 
    }
}
echo "CBTIS 89<br>";
echo "Array1 ------- Array2 ------- Array3<br>";

for ($i = 0; $i < count($array1); $i++) {
    echo $array1[$i] . " --------- ";
    echo (isset($array2[$i]) ? $array2[$i] : " ") . " -------- ";
    echo $array3[$i] . "<br>";
}
